 1.0\r\n";	$headers .= "From: " . $from . "\r\n";	$headers .= "Reply-To: " . $from . "\r\n";	$headers .= "Content-Type: multipart/mixed; boundary=\"$boundary\"\r\n";	$message = "Content-Type: multipart/mixed; boundary=\"$boundary\"		--$boundary		Content-Type: text/html; charset=\"utf-8\"		Content-Transfer-Encoding: 7bit		$message";	$message.="--$boundary--";		if (mail($from, $subject, $message, $headers)) {     		echo 'success';	} else {		echo $from;	}}?>